import React, { useState, useEffect } from 'react';

const ChatRoom = () => {
    const [userData, setUserData] = useState({
        username: '', // Initialize with an empty string
        connected: false,
        message: '',
    });

    useEffect(() => {
        // Load the Google API client library and initialize it with your Google OAuth client ID
        window.gapi.load('auth2', () => {
            window.gapi.auth2.init({
                client_id: '792979004767-5fvs24lfomcue1h532sqdluskt60ieql.apps.googleusercontent.com', // Replace with your Google OAuth client ID
            });
        });
    }, []);

    const handleGoogleSignIn = () => {
        window.gapi.auth2.getAuthInstance().signIn().then(
            (googleUser) => {
                // Get the authenticated user's basic profile information
                const profile = googleUser.getBasicProfile();
                const username = profile.getName();

                // Update the user data in the state
                setUserData({
                    ...userData,
                    username,
                    connected: true,
                });
            },
            (error) => {
                console.error('Google Sign-In Error:', error);
            }
        );
    };

    return (
        <div className="container">
            {userData.connected ? (
                <div>
                    <p>Welcome, {userData.username}!</p>
                    {/* Add chat room components here */}
                </div>
            ) : (
                <div className="register">
                    <button type="button" onClick={handleGoogleSignIn}>
                        Sign in with Google
                    </button>
                </div>
            )}
        </div>
    );
};

export default ChatRoom;
